import { renderData } from './RenderData';

const APIBase = 'https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/';

export async function fetchData(id) {
    const API = `${APIBase}${id}/`;
    try {
        const response = await fetch(API);
        const data = await response.json();
        //console.log(API);
        renderData(data);
        
    } catch (error) {
        console.error('ERROR');
    }
}

export async function fetchLocation(data) {
    const API = `${APIBase}search/?`;
    const API_URL = `${API}lattlong=${data.lat},${data.long}`;
    let header = new Headers({
        'Content-Type' : 'text/plain',
        'origin' : 'X-Requested-With',
    });
    try {
        const response = await fetch(API_URL, {
            method: 'GET',
            headers: header,
            mode: 'cors',
            cache: 'default',
        });
        const data = await response.json();

        if(data[0].woeid) {
            fetchData(data[0].woeid);
        }

    } catch(error) {
        console.error(error);
        return
    }
}
