    try {
        // const response = await fetch(API);
        // const data = await response.json();
        console.log(API);
        //console.log(data);
    } catch (error) {
        console.log('ERROR');
    }
}

export async function fetchLocation(long, lat) {
    const API = `${APIBase}search/?`;
    const API_URL = `${API}lattlong=${lat},${long}`;
    let header = new Headers();
    let myInit = {
        method: 'GET',
        headers: header,
        mode: 'cors',
        cache: 'default'
    };
    try {
        const response = await fetch(API_URL, myInit);
        const data = await response.json();



        console.log(data);

    } catch(error) {
        console.error(error);
        return
    }
}

export default fetchLocation;
