const APIBase = 'https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/';

export async function fetchData(id) {
    const API = `${APIBase}${id}/`;
    try {
        const response = await fetch(API);
        const data = await response.json();
        // console.log(data);
        //console.log(data);
    } catch (error) {
        console.log('ERROR');
    }
}

export async function fetchLocation(long, lat) {
    const API = `${APIBase}search/?`;
    const API_URL = `${API}lattlong=${lat},${long}`;
    let header = new Headers({
        'Content-Type' : 'text/plain',
        'origin' : 'x-requested-with',
    });
    let myInit = {
        method: 'GET',
        headers: header,
        mode: 'cors',
        cache: 'default',
    };
    var request = new Request(API_URL,myInit);
    try {
        const response = await fetch(request, myInit);
        const data = await response.json();



        if(data[0].woeid) {
            fetchData(data[0].woeid);
        }

        console.log(data);
        //console.log(API_URL);

    } catch(error) {
        console.error(error);
        return
    }
}

export default fetchLocation;
