export function GeoLocation() {

    function success() {
        console.log('Sucesso garai')
    }

    function error() {
        console.log('Falhou garai')
    }

    navigator.geolocation.getCurrentPosition(success, error);

    const watcher = navigator.geolocation.watchPosition(function(position) { console.log(position)})
}
