import React, { useState, useEffect } from "react";
import { Sidebar } from '../pages/Sidebar';

export function City(data) {

    const city = data.title;

    Sidebar(city);
    
    let [getCity, setCity] = useState("Desconhecido!")
    
    async function funCity() {
        setCity(await city);
    }

    useEffect(() => {
        if(city !== undefined) {
            funCity();
        }
    });
    
    return(
    <div>
        <p>{getCity}</p>
    </div>
    );
}
