import { GeoLocation } from "./Geolocation";
const APIBase = 'https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/';

let header = new Headers();
let myInit = {
    method: 'GET',
    headers: header,
    mode: 'cors',
    cache: 'default'
};

export async function fetchData(id) {
    const API = `${APIBase}${id}/`;
    try {
        // const response = await fetch(API, myInit);
        // const data = await response.json();
        console.log();
        //console.log(data);
    } catch (error) {
        console.log('ERROR');
    }
}

export async function fetchLocation(long, lat) {
    const API = `${APIBase}search/?`;
    const API_URL = `${API}lattlong=${lat},${long}`;
    try {
        const response = await fetch(API_URL, myInit);
        const data = await response.json();

        console.log(data);

    } catch(error) {
        console.error(error);
        return
    }
}

export default fetchLocation;
